import React, { Component } from 'react';
import YveBot from 'yve-bot/ui';
import 'yve-bot/ext/types/StringSearch';
import './Chat.css';

class Chat extends Component {
  constructor(props) {
    super(props);

    this.bot = new YveBot(props.rules || [], {
      target: '.Chat',
    });

    // Add event listener for when the bot asks for free text input
    this.bot.on('message', (message) => {
      if (message.type === 'SingleChoice' && !message.value) {
        this.bot.ask('Enter your custom query:');
      }
    });
  }

  componentDidMount() {
    this.bot.start();
  }

  render() {
    return (
      <div className="Chat" />
    );
  }
}

export default Chat;
