import React, { Component } from 'react';
import Chat from './Chat';
import yaml from 'js-yaml';
const file = fs.readFileSync('./chat.yaml', 'utf8');
const rules = yaml.safeLoad(file);
rules = [
  { type: 'String', message: 'Please Enter your SSO ID:', name: 'ssoid'},
  { type: 'String', message: 'Please Enter your Modality:', name: 'modality'},
  "Please feel to ask us any of the queries",
];
new YveBot(rules);


class App extends Component {
  render() {
    return (
      <div className="App">
        <Chat rules={rules} />
      </div>
    );
  }
}

export default App;
